'use client';

import React, { forwardRef, useRef } from 'react';

import { cn } from '@/lib/utils';
import { AnimatedBeam } from '@/components/ui/animated-beam';
import Image from 'next/image';

const Circle = forwardRef<HTMLDivElement, { className?: string; children?: React.ReactNode }>(
  ({ className, children }, ref) => {
    return (
      <div
        ref={ref}
        className={cn(
          'z-10 flex size-12 items-center justify-center rounded-full border-2 border-emerald-600 bg-white dark:border-emerald-900 dark:bg-neutral-900 p-2 shadow-[0_0_20px_-12px_rgba(0,0,0,0.8)]',
          className
        )}
      >
        {children}
      </div>
    );
  }
);
Circle.displayName = 'Circle';

export const SkillsBeam = () => {
  const containerRef = useRef<HTMLDivElement>(null);
  const div1Ref = useRef<HTMLDivElement>(null);
  const div2Ref = useRef<HTMLDivElement>(null);
  const div3Ref = useRef<HTMLDivElement>(null);
  const div4Ref = useRef<HTMLDivElement>(null);
  const div5Ref = useRef<HTMLDivElement>(null);
  const div6Ref = useRef<HTMLDivElement>(null);
  const div7Ref = useRef<HTMLDivElement>(null);

  return (
    <div
      className='relative flex h-[300px] w-full items-center justify-center overflow-hidden p-10 mb-6'
      ref={containerRef}
    >
      <div className='flex size-full max-h-[200px] max-w-lg flex-col items-stretch justify-between gap-10'>
        <div className='flex flex-row items-center justify-between'>
          <Circle ref={div1Ref}>
            <Icons.nextjs />
          </Circle>
          <Circle ref={div5Ref}>
            <Icons.typescript />
          </Circle>
        </div>
        <div className='flex flex-row items-center justify-center'>
          {/* <Circle ref={div2Ref}>
            <Icons.nodejs />
          </Circle> */}
          <Circle ref={div4Ref} className='size-16 p-1'>
            <Icons.op />
          </Circle>
          {/* <Circle ref={div6Ref}>
            <Icons.postgres />
          </Circle> */}
        </div>
        <div className='flex flex-row items-center justify-between'>
          <Circle ref={div3Ref}>
            <Icons.tailwind />
          </Circle>
          <Circle ref={div7Ref}>
            <Icons.github />
          </Circle>
        </div>
      </div>

      <AnimatedBeam containerRef={containerRef} fromRef={div1Ref} toRef={div4Ref} curvature={-75} endYOffset={-10} />
      {/* <AnimatedBeam containerRef={containerRef} fromRef={div2Ref} toRef={div4Ref} /> */}
      <AnimatedBeam containerRef={containerRef} fromRef={div3Ref} toRef={div4Ref} curvature={75} endYOffset={10} />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div5Ref}
        toRef={div4Ref}
        curvature={-75}
        endYOffset={-10}
        reverse
      />
      {/* <AnimatedBeam containerRef={containerRef} fromRef={div6Ref} toRef={div4Ref} reverse /> */}
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div7Ref}
        toRef={div4Ref}
        curvature={75}
        endYOffset={10}
        reverse
      />
    </div>
  );
};

